mapper.select:
SELECT  /* Ara3.selectChkNmMblEml */
	A.ROWNO
	, A.YYMMDD
	, B.USR_NM
	, C.MBL_NUM
	, D.EML_ADDR
FROM
	(
		SELECT ROWNUM AS ROWNO, TO_CHAR(SYSDATE, 'YYMMDD') AS YYMMDD FROM DUAL
	) A
		LEFT JOIN (
			SELECT ROWNUM AS ROWNO, USR_NM FROM ARA3_USR WHERE 1=1 AND USR_NM = #{usrName}
		) B ON B.ROWNO = A.ROWNO
		LEFT JOIN (
			SELECT ROWNUM AS ROWNO, MBL_NUM FROM ARA3_MBL WHERE 1=1 AND ITM_TYP = 'USR' AND MBL_NUM = #{usrMobile}
		) C ON C.ROWNO = A.ROWNO
		LEFT JOIN (
			SELECT ROWNUM AS ROWNO, EML_ADDR FROM ARA3_EML WHERE 1=1 AND ITM_TYP = 'USR' AND EML_ADDR = #{usrEmail}
		) D ON D.ROWNO = A.ROWNO
WHERE 1=1
